        ${PSQL} -c "CREATE INDEX idx_tiger_data_${ABBR}_faces_countyfp ON tiger_data.${ABBR}_faces USING btree (countyfp);"                                                                                                                                                                                                                                                                                                                                                 
        ${PSQL} -c "ALTER TABLE tiger_data.${ABBR}_faces ADD CONSTRAINT chk_statefp CHECK (statefp = '${FIPS}');"                                                                                                                                                                                                                                                                                                                                                           
