${PSQL} -c "CREATE TABLE tiger_data.${ABBR}_featnames(CONSTRAINT pk_${ABBR}_featnames PRIMARY KEY (gid)) INHERITS(tiger.featnames);ALTER TABLE tiger_data.${ABBR}_featnames ALTER COLUMN statefp SET DEFAULT '${FIPS}';"                                                                                                                                                                                                                                                    
