# Edges
#############   
cd $GISDATA                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
cd $GISDATA/www2.census.gov/geo/tiger/TIGER2017/EDGES/                                                                                                                                                                                                                                                                                                                                                                                                                      
rm -f ${TMPDIR}/*.*                                                                                                                                                                                                                                                                                                                                                                                                                                                         
${PSQL} -c "DROP SCHEMA IF EXISTS tiger_staging CASCADE;"                                                                                                                                                                                                                                                                                                                                                                                                                   
${PSQL} -c "CREATE SCHEMA tiger_staging;"                                                                                                                                                                                                                                                                                                                                                                                                                                   
